import React, { useState } from 'react';
import Navbar from './Navbar';
import Form from './Form';


const App = () =>{
    const [mode , setmode] = useState("light")
    const togglemode = () =>{
        if(mode === 'light')
        setmode('dark');
        else
        setmode('light');
    };
    return (
        <>
        <Navbar mode = {mode}  togglemode = {togglemode}/>
        
        <div className="container">
            <Form/>
        </div>
        </>
    );
}

export default App;
