import React,{useState} from 'react'
import Watchlist from './Watchlist';
// import Navbar from './Navbar';
import Cards from './Cards';
import Mcards from './Mcards';
import Wcards from './Wcards';
import { Route, Switch } from 'react-router-dom';
import { NavLink } from 'react-router-dom';


function App() {

  const Home = () => {
    return (<>
    <div className = "myfav">
    <h2 className= "filter">Filter</h2>
        <NavLink exact to="/Suggestions" activeClassName="selected" className = "fav"> Trending </NavLink>
        <NavLink exact to="/movies" activeClassName="selected" className = "fav"> Movies </NavLink>

        <NavLink exact to="/web" activeClassName="selected" className = "fav"> Web-Series </NavLink>
         {/* <NavLink exact t0 = "/wl"  activeClassName = "selected" className = "fav" > WatchList</NavLink> */}
         <NavLink exact to="/watch" activeClassName="selected" className = "fav"> Watch-Lists </NavLink>
        </div> 

    </>
    );
}
const [caa, setcaa] = useState([]);
     fetch("https://api.themoviedb.org/3/movie/popular?api_key=c9df0bb1b55da2720c721bcb3c0b5833").then((value)=>{
        // console.log(value);
       return value.json();
    }).then((currentdata)=>{
        console.log(currentdata);
       const Carr = currentdata.results;
    //    console.log(Carr);
       setcaa(Carr[0]);
    });

   console.log(caa);

  return (<>
    <h1 className = "tittle">Top Movies and Web-Series to Watch in 2021</h1>
    <div className = "WholeSection">
    <div className = "leftmenu">
     <Home/>

    </div>
    <div className = "cards">
    <Switch>
    <Route exact path= "/Suggestions" component = {Cards} />

    <Route exact path= "/movies" component ={Mcards} />
    <Route exact path= "/web" component ={Wcards} />
    <Route exact path= "/watch" component ={Watchlist} />


    </Switch>
    </div>
    </div>
    <h3 className = "tittle1">@2021 Harsh Bhatnagar all Rights Reserved Terms and Conditions</h3>
    </>
  );
}

export default App;
