import React, { useState } from 'react'

import Navbar from './Navbar';
import Cards from './Cards';
import Mcards from './Mcards';
import Wcards from './Wcards';
import { Route, Switch } from 'react-router-dom';
import { NavLink } from 'react-router-dom';


function App() {
  const [incre , setincre] = useState(1);
  const bdana = () =>
  {
    setincre(incre+1);

  };
  <Navbar bdanaa = {this.bdana}/>
  const Home = () => {
    return (<>
    <div className = "myfav">
    <h2 className= "filter">Filter</h2>
        <NavLink exact to="/" activeClassName="selected" className = "fav"> home </NavLink>
        <NavLink exact to="/movies" activeClassName="selected" className = "fav"> Movies </NavLink>

        <NavLink exact to="/web" activeClassName="selected" className = "fav"> Web-Series </NavLink>
        <h2>WatchList({incre})</h2>
        </div> 

    </>
    );
}

  return (<>
    <h1 className = "tittle">Hello I am Harsh Bhatnagar</h1>
    <div className = "WholeSection">
    <div className = "leftmenu">
     <Home/>

    </div>
    <div className = "cards">
    <Switch>
    <Route exact path= "/" component = {Cards} />

    <Route exact path= "/movies" component ={Mcards} />
    <Route exact path= "/web" component ={Wcards} />
    </Switch>
    </div>
    </div>
    </>
  );
}

export default App;
