import React from 'react';

const Navbar = (props) => {

   let  chngingNavbarTxtClr = ()=>{
        if(props.mode === 'dark' || props.num ===1)
        return 'light';
        else
        return 'dark';
    }

     const chngingred = () =>{
         props.threecolor(1);
     }
     const chnginggreen = () =>{
        props.threecolor(3);
    }
    const chngingyellow = () =>{
        props.threecolor(2);
    }
    return (
        <>
            <nav className={`navbar navbar-expand-lg navbar-${props.num ===1 ? 'dark' :props.mode} bg-${props.num ===1 ? 'dark' :props.mode}`}>
                <div className="container-fluid">
                    <a className="navbar-brand" href="/">TextChngr</a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0" >
                            <li className="nav-item" >
                                <a className="nav-link active" aria-current="page" href="/">Home</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="/">About</a>
                            </li>

                        </ul>
                        {/* <form className="d-flex">
        <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
        <button className="btn btn-outline-primary" type="submit">Search</button>
      </form> */}
                        <div className="btn-group" role="group" aria-label="Basic mixed styles example">
                            <button type="button" className="btn btn-danger" onClick = {chngingred}>Red</button>
                            <button type="button" className="btn btn-warning" onClick = {chngingyellow}>Yellow</button>
                            <button type="button" className="btn btn-success" onClick = {chnginggreen}>Green</button>
                        </div>

                        <div className={`mx-2 form-check form-switch text-${props.mode === 'light' ? 'dark' : 'light'}`}>
                            <input className="form-check-input" type="checkbox" onClick={props.togglemode} id="flexSwitchCheckDefault" />
                            <label className="form-check-label" htmlFor="flexSwitchCheckDefault">Enable Dark Mode</label>
                        </div>
                    </div>
                </div>
            </nav>
        </>
    );
}
export default Navbar;
