import React, { useState } from 'react';

const Form = () => {
    const [text, setText] = useState('');

    const chngeupr = () => {

        let texta = text.toUpperCase();
        setText(texta);

    }

    const chngelwr = () => {
        let texta = text.toLowerCase();
        setText(texta);
    }

    const cleartxt = () => {
        setText("")
    }
    const lelo = (event) => {
        setText(event.target.value);
    }

    return (
        <>

            <div className="mb-3 my-3">
                <h1>Enter Text to Convert</h1>
                <textarea className="form-control" value={text} onChange={lelo} placeholder="Enter your text" id="exampleFormControlTextarea1" rows="3"></textarea>
            </div>

            <button type="button" class="btn btn-primary mx-1" onClick={chngeupr}>Convert to Uppercase</button>
            <button type="button" class="btn btn-primary mx-1" onClick={chngelwr}>Convert to Lowercase</button>
            <button type="button" class="btn btn-primary mx-1" onClick={cleartxt}>Clear Text</button>
            <div className="mb-3 my-1">
                <h1>Text Summary</h1>
                <p> word : {text.split(" ").length} characters : {text.length}</p>
                <h1>Preview</h1>
                <p>{text}</p>
            </div>
            <div class="form-check form-switch">
                <input class="form-check-input" type="checkbox" id="flexSwitchCheckDefault" />
                <label class="form-check-label" for="flexSwitchCheckDefault">Default switch checkbox input</label>
            </div>
        </>
    )
}
export default Form;
