            <div className="mb-3">
                <textarea className="form-control" value = {text} onChange= {lelo} placeholder= "Enter your text" id="exampleFormControlTextarea1" rows="3"></textarea>
            </div>
            <div className ="col-auto">
                <button type="submit" class="btn btn-primary mb-3" onClick = {chnge} >Change to upercase</button>
            </div>
        </>
    )
}
export default Form;
